import React, {useState} from 'react'
import { Box, Typography, Button, Container, TextField } from '@mui/material'
import Rating from '@mui/material/Rating';
import { REVIEW_ACTIONS, ReviewState } from '../../context/ReviewContext';

function Review() {
  /*
  const [product, setProduct] = useState("");
  const [review, setReview] = useState("");
  const [value, setValue] = useState(2);
  */
  const {revState, DispatchReview} = ReviewState();



  return (
    <Box sx={{ py: 6 }}>
      <Container
        sx={{
          '& .MuiTextField-root': { m: 1, width: { xs: '25ch', md: '50ch' } },
          pb: 5,
          width: "50%", m: 'auto', border: "1px solid gray", p: 2
        }}
      >
        <Box component="form" >
          <Typography variant='h6' sx={{ m: 1 }}>ADD Review On Product :</Typography>
          <TextField
            label="Product Name"
            multiline
            sx={{ width: { xs: "50%", md: "100%" } }}
            onChange={DispatchReview({type : REVIEW_ACTIONS.PRODUCT , payload:e.target.value})}
          />
          <TextField
            label="ADD Review"
            multiline
            sx={{ width: { xs: "50%", md: "100%" } }}
            rows={4}
            onChange={DispatchReview({type : REVIEW_ACTIONS.REVIEW , payload:e.target.value})}
          />
          <Box
            sx={{
              '& > legend': { mt: 2 },
              ml: 1,
              border: "1px solid gray",
              mt: 2,
              p: 2,
              width: '70%'
            }}
          >
            <Typography component="legend">Rating : </Typography>
            <Rating
              name="simple-controlled"
              value={value}

              onChange={DispatchReview({type : REVIEW_ACTIONS.RATING , payload:value})}
            />
          </Box>
          <Typography varaint='caption' sx={{ color: "red", m: 1 }} ></Typography>
          <Button
            type="submit"
            variant="contained"
            color="primary"
            sx={{ m: 2 }}
          >
            add Review
          </Button>
        </Box>
      </Container>
    </Box >
  )
}

export default Review
