import React, { createContext, useContext, useReducer } from 'react';
import { ShoppingReducer } from './Reducers';
const ShoppingContext = createContext();

export const SHOP_ACTIONS = {
    ADD_TO_CARD: "add product to the basket",
    BUY_PRODUCT: "buy product added"
}

const initialADDState = {
    Count : 0,
    totalPrice: 0,
    products : [],
}

/*    buy : {
        product : null,
        loading : false,
        error : null
    }, */

function ShoppingContextProvider({ children }) {
    const [shopsState, DispatchShop] = useReducer(ShoppingReducer, initialADDState);
    return (
        <ShoppingContext.Provider value={{ shopsState, DispatchShop }}>
            {children}
        </ShoppingContext.Provider>
    )
}

export default ShoppingContextProvider


export const ShoppingState = () => {
    return useContext(ShoppingContext);
}
