import React, { useState } from 'react';
import { Badge, IconButton, Menu } from '@mui/material';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import RemoveShoppingCartIcon from '@mui/icons-material/RemoveShoppingCart';
import { ShoppingState } from '../../../context/ShoppingContext';

function Basket() {
    const { shopsState } = ShoppingState();

    const [BasketNav, setBasketNav] = useState(null);
    const user = JSON.parse(localStorage.getItem("CurrentUser"));

    const handleOpenNavBasket = (event) => {
        setBasketNav(event.currentTarget);
    };

    const handleCloseNavBasket = () => {
        setBasketNav(null);
    };
    console.log(shopsState)
    /*sx={{
        color: "rgba(0, 0, 0, 0.8)",
    }}*/

    return (
        <>
            {!user ?
                <RemoveShoppingCartIcon sx={{ mr: 5 }} />
                :
                <>
                    <IconButton color="inherit" sx={{ mr: 5 }}>
                        <Badge badgeContent={ 0} color="secondary">
                            <ShoppingCartIcon onClick={handleOpenNavBasket} />
                        </Badge>
                    </IconButton>
                    <Menu
                        id="menu-appbar"
                        anchorEl={BasketNav}
                        anchorOrigin={{
                            vertical: 'bottom',
                            horizontal: 'left',
                        }}
                        keepMounted
                        transformOrigin={{
                            vertical: 'top',
                            horizontal: 'left',
                        }}
                        open={Boolean(BasketNav)}
                        onClose={handleCloseNavBasket}

                        PaperProps={{
                            sx: {
                                bgcolor: "rgba(0, 0, 0, 0.8)",
                            },
                        }}
                    >
                        {

                        }
                    </Menu>
                </>
            }
        </>
    )
}

export default Basket
