const ACTIONS ={
    PRICE : "price",
    PRODUCER : "categorie",
    CATEGORY : "producer"
}

export const filterProdReducer = (state ,action)=>{
    switch(action.type){
        case ACTIONS.PRICE :
            const filtredprice = state.filter(st => st.price < action.payload);
            return {...filtredprice};
        ;
        case ACTIONS.PRODUCER : 
            const filtredproducer = state.filter(st => st.type == action.payload);
            return {...filtredproducer};
        ;
        case ACTIONS.CATEGORY : 
            const filtredcategory = state.filter(st => st.categorie > action.payload);
            return {...filtredcategory};
        ;
    }
}
