import React, { useRef, useState } from 'react'
import { Box, TextField, Container, Typography, Button } from '@mui/material'
import useFetch from '../../hooks/useFetch';


function Contact() {
    const [contact, setContact] = useState("");
    const opinionRef = useRef(null);
    const errorDescription = useRef(null);

    const {data, loading, error} = useFetch("/contacts")

    const handleSubmit = (event) => {
        event.preventDefault()

        const message = opinionRef.current.value;
        console.log(opinionRef.current.value)
        if (message.length < 8) {
            return errorDescription.current.innerHTML = "Your Contact Message Must Contain at least 8 Caracters.";
        } else {
            errorDescription.current.innerHTML = "";
        }


    }



    return (
        <Box sx={{ bgcolor: "silver", }}>
            <Container
                sx={{
                    '& .MuiTextField-root': { m: 1, width: { xs: '25ch', md: '50ch' } },
                    pb: 20,
                    pt: 10,
                    width: "50%", m: 'auto'
                }}
            >


                <form onSubmit={handleSubmit}>
                    <Typography variant='h6' sx={{ m: 1 }}>Contact us :</Typography>
                    <TextField
                        label="Contact us"
                        multiline
                        rows={5}
                        onChange={(e) => setContact(e.target.value)}
                        inputRef={opinionRef}
                    />
                    <Typography varaint='caption' ref={errorDescription} sx={{ color: "red", m: 1 }} ></Typography>
                    <Button
                        type="submit"
                        variant="contained"
                        color="primary"
                        sx={{m:2}}
                    >
                        add contact
                    </Button>
                </form>

            </Container>
        </Box>
    )
}

export default Contact
