import React, { useState, useEffect } from 'react';
import { Typography, Box, Container, Grid, Paper, Divider, Button } from '@mui/material';
import useFetch from "../../hooks/useFetch.js";
import ProductsList from '../../components/productsList/ProductsList';
import Loading from '../../components/Loading/Loading';
import List from '@mui/material/List';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';
import Collapse from '@mui/material/Collapse';
import StoreIcon from '@mui/icons-material/Store';
import ExpandLess from '@mui/icons-material/ExpandLess';
import ExpandMore from '@mui/icons-material/ExpandMore';
import Checkbox from '@mui/material/Checkbox';
import FormGroup from '@mui/material/FormGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import Slider from '@mui/material/Slider';

import "./style.css";

function valuetext(value) {
  return `${value}Â°C`;
}
const minDistance = 10;

function MinimumDistanceSlider() {
  const [value1, setValue1] = useState([0, 90]);

  const handleChange1 = (event, newValue, activeThumb) => {
    if (!Array.isArray(newValue)) {
      return;
    }

    if (activeThumb === 0) {
      setValue1([Math.min(newValue[0], value1[1] - minDistance), value1[1]]);
    } else {
      setValue1([value1[0], Math.max(newValue[1], value1[0] + minDistance)]);
    }
  };

  return (
    <Box sx={{ width: '80%' }}>
      <Slider
        getAriaLabel={() => 'Minimum distance'}
        value={value1}
        onChange={handleChange1}
        valueLabelDisplay="auto"
        getAriaValueText={valuetext}
        disableSwap
      />
      <Typography variant='h6' >{value1} Products</Typography>
      <Button variant='contained' sx={{ mb: 1 }}>Filter</Button>
    </Box>
  );
}

const ItelList = ({ Logo, Name }) => {
  let init = false;
  Name == "Producer" ? init = true : init = false;
  const [open, setOpen] = React.useState(init);
  const handleClick = () => {
    setOpen(!open);
  };
  console.log("ssss")
  return (
    <>
      <ListItemButton onClick={handleClick} sx={{ mt: 3 }}>
        <ListItemText primary={Name} />
        {open ? <ExpandLess /> : <ExpandMore />}
      </ListItemButton>
      <Collapse in={open} timeout="auto" unmountOnExit>
        <FormGroup sx={{ pl: 4 }}>

          {Name === 'Producer' &&
            <>
              <FormControlLabel control={<Checkbox />} label="ASUS" />
              <FormControlLabel control={<Checkbox />} label="LENOVO" />
            </>
          }

          {Name === 'Price' &&
            <>
              <MinimumDistanceSlider />
            </>
          }

          {Name === 'Availablity' &&
            <>
              <FormControlLabel control={<Checkbox />} label="Yes" />
              <FormControlLabel control={<Checkbox />} label="No" />
            </>
          }


          {Name === 'Gamer' &&
            <>
              <FormControlLabel control={<Checkbox />} label="Yes" />
              <FormControlLabel control={<Checkbox />} label="No" />
            </>
          }

          {Name === 'System' &&
            <>
              <FormControlLabel control={<Checkbox />} label="Windows" />
              <FormControlLabel control={<Checkbox />} label="LINUX" />
              <FormControlLabel control={<Checkbox />} label="MacOS" />
              <FormControlLabel control={<Checkbox />} label="CHROME OS" />
            </>
          }

          {Name === 'RAM' &&
            <>
              <FormControlLabel control={<Checkbox />} label="16Go" />
              <FormControlLabel control={<Checkbox />} label="12Go" />
              <FormControlLabel control={<Checkbox />} label="8Go" />
              <FormControlLabel control={<Checkbox />} label="4Go" />
            </>
          }

          {Name === 'PROCESSOR' &&
            <>
              <FormControlLabel control={<Checkbox />} label="Intel Core i5" />
              <FormControlLabel control={<Checkbox />} label="Intel Core i3" />
              <FormControlLabel control={<Checkbox />} label="Intel Core i7" />
              <FormControlLabel control={<Checkbox />} label="Apple M2" />
              <FormControlLabel control={<Checkbox />} label="Intel Celeron" />
            </>
          }

          {Name === 'GRAPHICS' &&
            <>
              <FormControlLabel control={<Checkbox />} label="Integrated Chart" />
              <FormControlLabel control={<Checkbox />} label="Nvidia GeForce" />
              <FormControlLabel control={<Checkbox />} label="AMD Radeon" />

            </>
          }

          {Name === 'HARD DISK' &&
            <>
              <FormControlLabel control={<Checkbox />} label="512Go SSD" />
              <FormControlLabel control={<Checkbox />} label="256 Go SSD" />
              <FormControlLabel control={<Checkbox />} label="1 To HDD" />
              <FormControlLabel control={<Checkbox />} label="1 To SSD" />
              <FormControlLabel control={<Checkbox />} label="128 Go SSD " />
              <FormControlLabel control={<Checkbox />} label="2To SSD " />
            </>
          }

          {Name === 'COLOR' &&
            <>
              <FormControlLabel control={<Checkbox />} label="Black" />
              <FormControlLabel control={<Checkbox />} label="White" />
              <FormControlLabel control={<Checkbox />} label="Red" />
              <FormControlLabel control={<Checkbox />} label="Gray" />
            </>
          }
        </FormGroup>
      </Collapse>
      <Divider variant="middle" flexItem />
    </>
  )
}



function Products() {
  const { data, loading, error } = useFetch(`/products`);

  return (
    <React.Fragment>
      <Grid container spacing={2}>
        <Grid item xs={3} sx={{ bgcolor: "rgba(0,0,0,0.2)", color: "black", pt: 10 }}>
          <List
            sx={{ width: '100%', maxWidth: 360, bgcolor: 'background.paper' }}
            component="nav"
            aria-labelledby="nested-list-subheader"
          >
            <Typography sx={{ color: "white", bgcolor: "#B22222", fontWeight: "bold", letterSpacing: 5, textAlign: 'center', py: 1 }}>
              Filter Products :
            </Typography>

            <ItelList Logo={StoreIcon} Name="Producer" />
            <ItelList Logo={StoreIcon} Name="Price" />
            <ItelList Logo={StoreIcon} Name="Availablity" />
            <ItelList Logo={StoreIcon} Name="Gamer" />
            <ItelList Logo={StoreIcon} Name="System" />
            <ItelList Logo={StoreIcon} Name="RAM" />
            <ItelList Logo={StoreIcon} Name="PROCESSOR" />
            <ItelList Logo={StoreIcon} Name="GRAPHICS" />
            <ItelList Logo={StoreIcon} Name="HARD DISK" />
            <ItelList Logo={StoreIcon} Name="COLOR" />
          </List>
        </Grid>
        <Grid item xs={9} sx={{ bgcolor: "rgba(0,0,0,0.2)", color: "black", pt: 10 }}>
          <>
            {loading ?

              <Loading />
              :
              <Box >

                <Container >
                  <ProductsList data={data} />
                  <ProductsList data={data} />
                  <ProductsList data={data} />

                </Container>

              </Box>
            }

          </>
        </Grid>
      </Grid>

    </React.Fragment>
  )
}

export default Products
