import React, { useState, useEffect } from 'react';
import { Typography, Box, Container, Grid, Paper } from '@mui/material';
import useFetch from "../../hooks/useFetch.js";
import ProductsList from '../../components/productsList/ProductsList';
import Loading from '../../components/Loading/Loading';
import { styled } from '@mui/material/styles';

import "./style.css";

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: 'center',
  color: theme.palette.text.secondary,
}));

function Products() {
  /*
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(false);
  const url  ="/products"
  useEffect(()=>{
      const FetchApiData = async()=>{
      setLoading(true);
      console.log(loading)
      try{
          const res = await axios.get(url);
          setData(res.data);
      }catch(error){
          setError(error);
          console.log(error.message);
      }
      setLoading(false);
  };
  FetchApiData();
  }, [url])
  */
  const { data, loading, error } = useFetch(`/products`);

  return (
    <React.Fragment>
      <Grid container spacing={2}>

        <Grid item xs={3}>
          <Item>xs=4</Item>
        </Grid>
        <Grid item xs={9}>
            {loading ?

              <Loading />
              :
              <Box sx={{ bgcolor: "rgba(0,0,0,0.2)", color: "black", pt: 10 }}>

                <Container >
                  <Typography variant='h3' sx={{ textAlign: 'center' }}>Laptops : </Typography>
                  <Typography variant='h6' sx={{ textAlign: 'center', color: "purple" }}>we only have 20 laptops for sale</Typography>
                  <ProductsList data={data} />
                  <Typography variant='h3' sx={{ textAlign: 'center', mt: 5 }}>Desktops : </Typography>
                  <Typography variant='h6' sx={{ textAlign: 'center', color: "purple" }}>we only have 20 Desktops for sale</Typography>
                  <ProductsList data={data} />
                  <Typography variant='h3' sx={{ textAlign: 'center', mt: 5 }}>Phones : </Typography>
                  <Typography variant='h6' sx={{ textAlign: 'center', color: "purple" }}>we only have 20 Phones for sale</Typography>
                  <ProductsList data={data} />
                  <Typography variant='h3' sx={{ textAlign: 'center', mt: 5 }}>Other Products : </Typography>
                  <Typography variant='h6' sx={{ textAlign: 'center', color: "purple" }}>we only have 20 Products for sale</Typography>

                  <ProductsList data={data} />
                </Container>

              </Box>
            }

        </Grid>
      </Grid>

    </React.Fragment>
  )
}

export default Products
