import React, { useRef, useState } from 'react'
import { Box, TextField, Container, Typography, Button, FormControl } from '@mui/material'

function Contact() {
    const [contact, setContact] = useState("");
    const contactref = useRef(null);


    const handleSubmit = (event) => {
        event.preventDefault();
        if (contact === "") {
            console.log(contactref.current.value)
        }
    };


    return (
        <Box sx={{ bgcolor: "silver", }}>
            <Container
                component="form"
                sx={{
                    '& .MuiTextField-root': { m: 1, width: { xs: '25ch', md: '50ch' } },

                    pb: 20,
                    pt: 10,
                    width: "60%", m: 'auto'
                }}
                noValidate
                autoComplete="off"
            >


                <FormControl onSubmit={handleSubmit}>
                    <Typography variant='h6'>Contact us :</Typography>
                    <TextField
                        label="Contact us"
                        multiline
                        rows={5}
                        onChange={(e) => setContact(e.target.value)}
                        ref={contactref}
                    />
                    <Button variant='contained' sx={{ m: 1, display: "block" }} >Add Contact</Button>
                </FormControl>

            </Container>
        </Box>
    )
}

export default Contact
