import React, { Children, createContext, useContext, useEffect, useReducer } from 'react'
import { ContactReducer } from './Reducers';
import useFetch from '../hooks/useFetch';
const ContactContext = createContext();

export const CONTACT_ACTIONS = {
    START: "start page",
    ADD_CONTACT: "add",
    DELETE_CONTACT: "delete",
    UPDATE_CONTACT: "update",
}

const initialState = {
    contact: null,
    loading: false,
    error: null
}

function ContactContextProvider({ children }) {
    useEffect(()=>{
        const { data, loading, error } = useFetch(`/contacts/${userID}`);
        initialState = {
            contact : data,
            loading : loading,
            error : error,
        }
    }, [])

    const [ContState, DispatchContact] = useReducer(ContactReducer, initialState);

    return (
        <ContactContext.Provider value={{ ContState, DispatchContact }}>
            {children}
        </ContactContext.Provider>
    )
}

export default ContactContextProvider

export const ContactState = () => {
    return useContext(ContactContext);
}
