import React, {useState, useEffect } from "react";
import axios from "axios";

const useFetch = (url) => {
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(false);

    useEffect(()=>{
        const FetchApiData = async()=>{
        setLoading(true);
        try{
            const res = await axios.get(url);
            setData(res.data);
        }catch(error){
            setError(error);
        }
        setLoading(false);
    };
    FetchApiData();

    }, [url])
    return { data, loading, error };
}

export const useFetchDispatch = (url,  dispatchFilter,  setComputers) => {
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(false);

    useEffect(()=>{
        const FetchApiData = async()=>{
        setLoading(true);
        try{
            const res = await axios.get(url);
            setComputers(res.data)
            dispatchFilter({ type: "SET_COMPUTERS", payload: res.data });
        }catch(error){
            setError(error);
        }
        setLoading(false);
    };
    FetchApiData();

    }, [url])
    return { data, loading, error };
}


export default useFetch;
