import React, {useState, useRef } from 'react'
import { Box, Typography, Button, Container, TextField } from '@mui/material'
import Rating from '@mui/material/Rating';
import { REVIEW_ACTIONS, ReviewState } from '../../context/ReviewContext';
import axios from 'axios';

function Review() {
  /*
  const [product, setProduct] = useState("");
  const [review, setReview] = useState("");
  const [value, setValue] = useState(2);
  */
  const {revState, DispatchReview} = ReviewState();

  const errorProduct = useRef(null);
  const errorReview = useRef(null);
  const successMessage = useRef(null);
  const productRef = useRef(null);
  const reviewRef = useRef(null);
  const ratingRef = useRef(null);

  console.log(revState);

  const handle_addReview = async (e) => {
    e.preventDefault();
    const userID = JSON.parse(localStorage.getItem('CurrentUser'))._id;
    const reference =  productRef.current.value;
    console.log(reference);

    try{
        const product = await axios.post('/products/one', {data :{ref : reference} } );
        console.log(product)
    }catch(error){
        console.log(error.data);
        return
    }

    try {
        const responce = await axios.post(`/reviews/${userID}`, revState);
    } catch (error) {
        //alert(error.response.data.message ? error.response.data.message : "Someting Went Wrong")
    }

  }

  return (
    <Box sx={{ py: 6 }}>
      <Container
        sx={{
          '& .MuiTextField-root': { m: 1, width: { xs: '25ch', md: '50ch' } },
          pb: 5,
          width: "50%", m: 'auto', border: "1px solid gray", p: 2
        }}
      >
        <Box component="form" onSubmit={handle_addReview} >
          <Typography variant='h6' sx={{ m: 1 }}>ADD Review On Product :</Typography>
          <TextField
            inputRef={productRef}
            label="Product Name"
            multiline
            sx={{ width: { xs: "50%", md: "100%" } }}
            onChange={(e)=>DispatchReview({type : REVIEW_ACTIONS.PRODUCT , payload:e.target.value})}
          />
          <Typography varaint='caption' sx={{ color: "red", m: 1 }} ref={errorProduct}></Typography>
          <TextField
            inputRef={reviewRef}
            label="ADD Review"
            multiline
            sx={{ width: { xs: "50%", md: "100%" } }}
            rows={4}
            onChange={(e)=>DispatchReview({type : REVIEW_ACTIONS.REVIEW , payload:e.target.value})}
          />
          <Typography varaint='caption' sx={{ color: "red", m: 1 }} ref={errorReview}></Typography>
          <Box
            sx={{
              '& > legend': { mt: 2 },
              ml: 1,
              border: "1px solid gray",
              mt: 2,
              p: 2,
              width: '70%'
            }}
          >
            <Typography component="legend">Rating : </Typography>
            <Rating
              ref={ratingRef}
              name="simple-controlled"
              value={revState.revState}
              onChange={(event, newValue) => DispatchReview({type : REVIEW_ACTIONS.RATING , payload : newValue})}
            />
          </Box>
          <Typography varaint='caption' sx={{ color: "red", m: 1 }} ref={successMessage}></Typography>
          <Button
            type="submit"
            variant="contained"
            color="primary"
            sx={{ m: 2 }}
          >
            add Review
          </Button>
        </Box>
      </Container>
    </Box >
  )
}

export default Review
