import React, { useState, useContext } from 'react';
import { Typography, Box, Container, Grid, Button } from '@mui/material';
import useFetch from "../../hooks/useFetch.js";
import ProductsList from '../../components/productsList/ProductsList';
import Loading from '../../components/Loading/Loading';
import Filter from '../../components/filter/Filter.js';
import { FilterComputersState } from '../../context/filterProductContext.js';

import "./style.css";

function Products() {
  //const { data, loading, error } = useFetch(`/products`);

  const [open, setOpen] = useState(true);

  const { loading, error, state,handleCategoryToggle, handleTypeToggle } = FilterComputersState();
  /*
  const handleCheckboxChange = (event) => {
    const { name, checked } = event.target;

    const [filterType, filterName] = name.split("-");
    console.log(state)

    // Only allow one checkbox to be selected from each filter type.

    if (checked) {
      Object.keys(state.filters[filterType]).forEach((name) => {
        updateFilters(filterType, name, false);
      });
    }

    updateFilters(filterType, filterName, checked);
  };
  */
  console.log(state.computers)

  return (
    <React.Fragment>
      <Grid container spacing={2}>
      <Grid item xs={3} >

    <div>
      <h2>Categories</h2>
      <label>
        <input type="checkbox" checked={state.categories.laptop} onChange={() => handleCategoryToggle('laptop')} />
        Laptop
      </label>
      <label>
        <input type="checkbox" checked={state.categories.desktop} onChange={() => handleCategoryToggle('desktop')} />
        Desktop
      </label>

      <h2>Types</h2>
      <label>
        <input type="checkbox" checked={state.types.asus} onChange={() => handleTypeToggle('asus')} />
        Asus
      </label>
      <label>
        <input type="checkbox" checked={state.types.lenovo} onChange={() => handleTypeToggle('lenovo')} />
        Lenovo
      </label>
      <label>
        <input type="checkbox" checked={state.types.mac} onChange={() => handleTypeToggle('mac')} />
        Mac OS
      </label>
      <label>
        <input type="checkbox" checked={state.types.mac} onChange={() => handleTypeToggle('mac')} />
        DELL
      </label>
    </div>

    </Grid>
        <Grid item xs={9} sx={{ bgcolor: "rgba(0,0,0,0.2)", color: "black", pt: 10 }}>
          <>
            {loading ?

              <Loading />
              :
              <Box >

                <Container >
                  <ProductsList data={state.computers} />

                </Container>

              </Box>
            }

          </>
        </Grid>
      </Grid>

    </React.Fragment>
  )
}

export default Products
