import React, { useRef, useState } from 'react'
import { Box, TextField, Container, Typography, Button } from '@mui/material'
import useFetch from '../../hooks/useFetch';
import Loading from '../../components/Loading/Loading'
import axios from 'axios';
function Contact() {
    const [contact, setContact] = useState("");
    const opinionRef = useRef(null);
    const errorDescription = useRef(null);
    const userID = JSON.parse(localStorage.getItem('CurrentUser'))._id;
    console.log(userID)

    const { data, loading, error } = useFetch(`/contacts/${userID}`);

    console.log(data)

    const handleADDContact = async(event) => {
        event.preventDefault();
        const message = opinionRef.current.value;
        console.log(opinionRef.current.value);
        if (message.length < 8) {
            return errorDescription.current.innerHTML = "Your Contact Message Must Contain at least 8 Caracters.";
        } else {
            errorDescription.current.innerHTML = "";
        }

        const saved = await axios.post('/contacts/${userID}', contact)


    }

    return (
        <Box sx={{ bgcolor: "silver",pt:5 }}>
            <Container
                sx={{
                    '& .MuiTextField-root': { m: 1, width: { xs: '25ch', md: '50ch' } },
                    pb: 5,

                    width: "50%", m: 'auto'
                }}
            >


                <Box component="form" onSubmit={handleADDContact}>
                    <Typography variant='h6' sx={{ m: 1 }}>Contact us :</Typography>
                    <TextField
                        label="Contact us"
                        multiline
                        rows={5}
                        onChange={(e) => setContact(e.target.value)}
                        inputRef={opinionRef}
                    />
                    <Typography varaint='caption' ref={errorDescription} sx={{ color: "red", m: 1 }} ></Typography>
                    <Button
                        type="submit"
                        variant="contained"
                        color="primary"
                        sx={{ m: 2 }}
                    >
                        add contact
                    </Button>
                </Box>
            </Container>
            <Box sx={{ border: "1px solid back", width: "60%", m: "auto", pb: 10, }}>
                {loading ?
                    <Loading />
                    :
                    <>
                        {data.map((item, index) => (
                            <Box sx={{ color: "purple", border: "1px solid purple", m: 2, p: 2 }}>
                                <span>Contact NÂ°{index + 1} : </span>
                                <Typography variant="h6" sx={{ color: "rgba(0,0,0,0.8)" }}>{item.contact_message}</Typography>
                                <Button variant='outlined' color="error" sx={{ mt: 2 }}>DELETE</Button>
                                <Button variant='outlined' color="success" sx={{ mt: 2, ml: 5 }}>UPDATE</Button>
                            </Box>
                        ))}
                    </>
                }
            </Box>
        </Box>
    )
}

export default Contact
