import TextField from '@mui/material/TextField';
import { ShoppingState } from '../../context/ShoppingContext';
import { useLocation } from 'react-router-dom';
import { Grid } from '@mui/material';

function Card() {
    const { shopsState, DispatchShop } = ShoppingState();
    const location = useLocation();
    const productID = location.pathname.split("/")[2];
    const Product = shopsState.allProducts.filter(Product => Product.data._id === productID)
    console.log(Product)


    return (
        <Grid container spacing={3} >

            {Product.map((item) => (
                <>
                    <Grid xs={4}>

                        <img src={generatePhoto(item.data.type)} />

                    </Grid>

                    <Grid xs={4}>
                        <p>Name : {item.data.name}</p>
                        <p>Price : {item.data.price}</p>
                        <p>Type : {item.data.type}</p>
                        <p>Categorie : {item.data.categorie}</p>
                        <p>Description : {item.Quantity}</p>
                    </Grid>
                    <Grid xs={4}>

                        <p>Description : {description}</p>

                    </Grid>
                </>
            ))}
        </Grid>
    )
}

export default Card




