import React, { createContext, useContext, useReducer, useRef } from 'react'
import axios from 'axios';
import { ReviewReducer } from './Reducers';
export const ReviewContext = createContext();

export const REVIEW_ACTIONS = {
    PRODUCT: "product",
    RATING: "rating",
    REVIEW: "review",
    ADD_BUTT: "add review",
}

/*
    ADD_REVIEW :{

        loading: false,
    }
*/


const initialState = {
    product: "",
    rating: 0,
    review: "",


}

function ReviewContextProvider({ children }) {
    const [revState, DispatchReview] = useReducer(ReviewReducer, initialState);
    const userID = JSON.parse(localStorage.getItem('CurrentUser'))._id;

    const errorProduct = useRef(null);
    const errorReview = useRef(null);
    const successMessage = useRef(null);

    const handle_addReview = async () => {
        try {
            const responce = await axios.post(`/reviews/${userID}`, revState);
        } catch (error) {
            alert(error.response.data.message)
        }
    }

    return (
        <ReviewContext.Provider value={{ revState,errorProduct, errorReview, successMessage, handle_addReview, DispatchReview }}>
            {children}
        </ReviewContext.Provider>
    )
}

export default ReviewContextProvider;

export const ReviewState = () => {
    return useContext(ReviewContext);
}
